mapper.select:
SELECT N.NB_ID, N.CATE_ID, C.CATE_NM, N.TTL, N.CN, N.WRTR, N.REG_DTM, N.UPD_DTM, N.DEL_DTM, N.VIEWS, N.STUS
FROM NOTICE N, BBS_CATEGORY C
WHERE N.CATE_ID = C.CATE_ID AND NB_ID = #{NB_ID}

mapper.delete:
        DELETE FROM NOTICE
        WHERE NB_ID = #{NB_ID}
#         AND WRTR='관리자'

mapper.select:
SELECT N.NB_ID, N.CATE_ID, C.CATE_NM, N.TTL, N.CN, N.WRTR, N.REG_DTM, N.UPD_DTM, N.DEL_DTM, N.VIEWS, N.STUS
FROM NOTICE N, BBS_CATEGORY C
WHERE N.CATE_ID = C.CATE_ID AND N.CATE_ID = #{CATE}
ORDER BY REG_DTM DESC, NB_ID DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM NOTICE
WHERE CATE_ID=#{CATE_ID}

mapper.update:
UPDATE NOTICE
SET CATE_ID = #{CATE_ID}
    , STUS = #{STUS}
    , WRTR = #{WRTR}
    , TTL = #{TTL}
    , CN = #{CN}
WHERE NB_ID = #{NB_ID}
